package PRP;

public class MinMax {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Integer max1 = Integer.MAX_VALUE;
		Integer min1 = Integer.MIN_VALUE;
		System.out.println("Integer range:");
		System.out.println("min:"+max1);
		System.out.println("max:"+min1 );
		Double min2 = Double.MAX_VALUE;
		Double max2 = Double.MIN_VALUE;
		System.out.println("Double range:");
		System.out.println("min:"+min2);
		System.out.println("max:"+max2);
		Long min3 = Long.MAX_VALUE;
		Long max3 = Long.MIN_VALUE;
		System.out.println("Float range:");
		System.out.println("min:"+min3);
		System.out.println("max:"+max3);
		Short min4 = Short.MAX_VALUE;
		Short max4 = Short.MIN_VALUE;
		System.out.println("Short range:");
		System.out.println("min:"+min4);
		System.out.println("max:"+max4);
		Byte min5 = Byte.MIN_VALUE;
		Byte max5 = Byte.MAX_VALUE;
		System.out.println("Byte range:");
		System.out.println("min:"+min5);
		System.out.println("max:"+max5);
		Float max6 = Float.MAX_VALUE;
		Float min6 = Float.MIN_VALUE;
		System.out.println("Float range:");
		System.out.println("min:"+min6);
		System.out.println("max:"+max6);
		
	}
}

	
